################################################################################
## Benchmark timings (DO NOT EDIT THIS FILE)

library(microbenchmark)

source("HW5_Class.R")

## Create very large vectors (dense format)
x0 <- c(1, rep(0, 1e7))
y0 <- c(rep(0, 1e7), 1)

## Convert vectors to 'sparse_numeric' class
x <- as(x0, "sparse_numeric")
y <- as(y0, "sparse_numeric")

## Run benchmarks

## Compare addition
add_bench <- microbenchmark(x + y, x0 + y0)

## Compare subtraction
sub_bench <- microbenchmark(x - y, x0 - y0)

## Compare multiplication
mult_bench <- microbenchmark(x * y, x0 * y0)

# print results
add_bench
sub_bench
mult_bench
